import React, { useState } from 'react';
import Head from 'next/head';
import AppLayout from '../components/Applayout';
import { Form, Input, Checkbox, Button } from 'antd';

const Signup = () => {
    const {id, setId} = useState('');
    const {nick, setNick} = useState('');
    const {password, setPassword} = useState('');
    const {passwordChk, setPasswordChk} = useState('');
    const {term, setTerm} = useState(false);

    const onSubmit = () => {

    };

    const onChangeId = () => {

    };

    const onChangeNick = () => {

    };

    const onChangePassword = () => {

    };

    const onChangePasswordChk = () => {

    };

    const onChangeTerm = () => {

    };


    return (
        <>
            <Head>
                <title>NodeBird</title>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.23.5/antd.css" />
                <script src="https://cdnjs.cloudflare.com/ajax/libs/antd/3.23.5/antd.js" />
            </Head>
            <AppLayout>
                <Form onSubmit={onSubmit} style={{ padding: 10 }}>
                    <div>
                        <label htmlFor="user-id">아이디</label>
                        <br/>
                        <Input name="user-id" required onChange={onChangeId} />
                    </div>
                    <div>
                        <label htmlFor="user-nick">닉네임</label>
                        <br/>
                        <Input name="user-nick" required onChange={onChangeNick} />
                    </div>
                    <div>
                        <label htmlFor="user-pw">비밀번호</label>
                        <br/>
                        <Input name="user-pw" required onChange={onChangePassword} />
                    </div>
                    <div>
                        <label htmlFor="user-pw-chk">비밀번호 확인</label>
                        <br/>
                        <Input name="user-pw-chk" required onChange={onChangePasswordChk} />
                    </div>
                    <div>
                        <Checkbox name="user-term" onChange={onChangeTerm}>혜지니 말을 잘 들을 것을 동의합니다.</Checkbox>
                    </div>
                    <div>
                        <Button type="primary" htmlType="submit">가입하기</Button>
                    </div>
                </Form>
            </AppLayout>
        </>
    );
}

export default Signup;
